import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RailroadCrossing } from '../home/home.component';
import { RailroadCrossingsService } from '../services/railroad-crossings.service';

@Component({
  selector: 'app-edit-railroad',
  templateUrl: './edit-railroad.component.html',
  styleUrls: ['./edit-railroad.component.css']
})
export class EditRailroadComponent implements OnInit {

  railroadCrossingDetail: RailroadCrossing = {
    id: 0,
    fld_naam_ramses: '',
    fld_actief_passief: '',
    type_pn: '',
    fld_geo_x: '',
    fld_geo_y: '',
    fld_postcode_en_gemeente: '',
    position_du_passage_a_niveau: {
      lat: 0,
      lon: 0
    },
    type_lc: '',
  }

  constructor(private route: ActivatedRoute, private railroadCrossingsService: RailroadCrossingsService, private router: Router) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');
        if (id) {
          this.railroadCrossingsService.getRailroadCrossingById(id).subscribe({
            next: (response) => {
              this.railroadCrossingDetail = response;
            }
          });
        }
      }
    })
  }

  updateRailroadCrossing() {
    this.railroadCrossingsService.updateRailroadCrossing(this.railroadCrossingDetail.id.toString(), this.railroadCrossingDetail)
      .subscribe({
        next: () => {
          this.router.navigate(['/']);
        }
      });
  }
}

<div class="container">
    <h1>Edit railroad crossing</h1>

    <form #form="ngForm" (ngSubmit)="updateRailroadCrossing()">
        <div class="mb-3">
            <label for="idInput" class="form-label">Id</label>
            <input type="text" class="form-control" id="idInput" name="id" [(ngModel)]="railroadCrossingDetail.id" readonly>
        </div>
        <div class="mb-3">
            <label for="nameInput" class="form-label">Name</label>
            <input type="text" class="form-control" id="nameInput" name="name" [(ngModel)]="railroadCrossingDetail.fld_naam_ramses">
        </div>
        <div class="mb-3">
        <label for="actiefPassiefInput" class="form-label">Active/Passive</label>
        <input type="text" class="form-control" id="actiefPassiefInput" name="actiefPassief" [(ngModel)]="railroadCrossingDetail.fld_actief_passief">
        </div>
        <div class="mb-3">
        <label for="typepnInput" class="form-label">Type</label>
        <input type="text" class="form-control" id="typepnInput" name="typepn" [(ngModel)]="railroadCrossingDetail.type_pn">
        </div>
        <div class="mb-3">
        <label for="typelcInput" class="form-label">Type</label>
        <input type="text" class="form-control" id="typelcInput" name="typelc" [(ngModel)]="railroadCrossingDetail.type_lc">
        </div>
        <div class="mb-3">
        <label for="positionXInput" class="form-label">Position X</label>
        <input type="text" class="form-control" id="positionXInput" name="positionX" [(ngModel)]="railroadCrossingDetail.fld_geo_x">
        </div>
        <div class="mb-3">
        <label for="positionYInput" class="form-label">Position Y</label>
        <input type="text" class="form-control" id="positionYInput" name="positionY" [(ngModel)]="railroadCrossingDetail.fld_geo_y">
        </div>
        <div class="mb-3">
        <label for="zipcodeInput" class="form-label">Zip code and municipality</label>
        <input type="text" class="form-control" id="zipcodeInput" name="zipcode" [(ngModel)]="railroadCrossingDetail.fld_postcode_en_gemeente">
        </div>
        <div class="mb-3">
        <label for="longitudeInput" class="form-label">Longitude</label>
        <input type="number" class="form-control" id="longitudeInput" name="longitude" [(ngModel)]="railroadCrossingDetail.position_du_passage_a_niveau.lon">
        </div>
        <div class="mb-3">
        <label for="latitudeInput" class="form-label">Latitude</label>
        <input type="number" class="form-control" id="latitudeInput" name="latitude" [(ngModel)]="railroadCrossingDetail.position_du_passage_a_niveau.lat">
        </div>
        <div>
            <button type="submit" class="btn btn-primary">Edit</button>
            <button type="button" class="btn btn-danger" id="deleteButton">Delete</button>
        </div>
    </form>
</div>
