import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { RailroadCrossing } from '../home/home.component';
import { RailroadCrossingsService } from '../services/railroad-crossings.service';

@Component({
  selector: 'app-add-railroad',
  templateUrl: './add-railroad.component.html',
  styleUrls: ['./add-railroad.component.css']
})
export class AddRailroadComponent implements OnInit {

  addRailroadCrossingRequest: RailroadCrossing = {
    id: 0,
    fld_naam_ramses: '',
    fld_actief_passief: '',
    type_pn: '',
    fld_geo_x: '',
    fld_geo_y: '',
    fld_postcode_en_gemeente: '',
    position_du_passage_a_niveau: {
      lat: 0,
      lon: 0
    },
    type_lc: '',
  };

  constructor(private railroadCrossingsService: RailroadCrossingsService, private router: Router) { }

  ngOnInit(): void {

  }

  addRailroadCrossing() {
    this.railroadCrossingsService.addRailroadCrossing(this.addRailroadCrossingRequest)
      .subscribe({
        next: () => {
          this.router.navigate(['/']);
        }
      });
  }

  validate() {
    var form = document.getElementsByClassName('needs-validation')[0] as HTMLFormElement;
    if (form.checkValidity() == false) {
      event?.preventDefault();
      event?.stopPropagation();
    }
    form.classList.add('was-validated');
  }
}

<div class="container">
    <h1>Add new railroad crossing</h1>

    <form #form="ngForm" class="needs-validation" (ngSubmit)="addRailroadCrossing()" novalidate>
        <div class="mb-3 has-validation">
            <label for="nameInput" class="form-label">Name</label>
            <input type="text" class="form-control" id="nameInput" name="name" [(ngModel)]="addRailroadCrossingRequest.fld_naam_ramses" required>
            <div class="invalid-feedback">
                Please choose a name.
            </div>
        </div>
        <div class="mb-3">
            <label for="actiefPassiefInput" class="form-label">Active/Passive</label>
            <input type="text" class="form-control" id="actiefPassiefInput" name="actiefPassief" [(ngModel)]="addRailroadCrossingRequest.fld_actief_passief" required>
            <div class="invalid-feedback">
                    This field is required.
            </div>
        </div>
        <div class="mb-3">
            <label for="typepnInput" class="form-label">Type_pn</label>
            <input type="text" class="form-control" id="typepnInput" name="typepn" [(ngModel)]="addRailroadCrossingRequest.type_pn" required>
            <div class="invalid-feedback">
                    Please choose a type.
            </div>
        </div>
        <div class="mb-3">
            <label for="typelcInput" class="form-label">Type_lc</label>
            <input type="text" class="form-control" id="typelcInput" name="typelc" [(ngModel)]="addRailroadCrossingRequest.type_lc" required>
            <div class="invalid-feedback">
                    Please choose a type.
            </div>
        </div>
        <div class="mb-3">
            <label for="positionXInput" class="form-label">Position X</label>
            <input type="text" class="form-control" id="positionXInput" name="positionX" [(ngModel)]="addRailroadCrossingRequest.fld_geo_x" required>
        </div>
        <div class="mb-3">
            <label for="positionYInput" class="form-label">Position Y</label>
            <input type="text" class="form-control" id="positionYInput" name="positionY" [(ngModel)]="addRailroadCrossingRequest.fld_geo_y" required>
        </div>
        <div class="mb-3">
            <label for="zipcodeInput" class="form-label">Zip code and municipality</label>
            <input type="text" class="form-control" id="zipcodeInput" name="zipcode" [(ngModel)]="addRailroadCrossingRequest.fld_postcode_en_gemeente" required>
        </div>
        <div class="mb-3">
            <label for="longitudeInput" class="form-label">Longitude</label>
            <input type="number" class="form-control" id="longitudeInput" name="longitude" [(ngModel)]="addRailroadCrossingRequest.position_du_passage_a_niveau.lon" required>
        </div>
        <div class="mb-3">
            <label for="latitudeInput" class="form-label">Latitude</label>
            <input type="number" class="form-control" id="latitudeInput" name="latitude" [(ngModel)]="addRailroadCrossingRequest.position_du_passage_a_niveau.lat" required>
        </div>
        <button type="submit" class="btn btn-primary" (click)="validate()">Save</button>
    </form>
</div>
