import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { RailroadCrossing } from '../home/home.component';
import { RailroadCrossingsService } from '../services/railroad-crossings.service';

@Component({
  selector: 'app-edit-railroad',
  templateUrl: './edit-railroad.component.html',
  styleUrls: ['./edit-railroad.component.css']
})
export class EditRailroadComponent implements OnInit {

  constructor(private route: ActivatedRoute, private railroadCrossingsService: RailroadCrossingsService) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');
        if (id) {
          this.railroadCrossingsService.getRailroadCrossingById(id);
        }
      }
    })
  }
}

<div class="container">
    <h1>Edit railroad crossing</h1>

    <form #form="ngForm">
        <div class="mb-3">
            <label for="nameInput" class="form-label">Name</label>
            <input type="text" class="form-control" id="nameInput" name="name" [(ngModel)]="addRailroadCrossingRequest.fld_naam_ramses">
        </div>
        <div class="mb-3">
        <label for="actiefPassiefInput" class="form-label">Active/Passive</label>
        <input type="text" class="form-control" id="actiefPassiefInput" name="actiefPassief" [(ngModel)]="addRailroadCrossingRequest.fld_actief_passief">
        </div>
        <div class="mb-3">
        <label for="typepnInput" class="form-label">Type</label>
        <input type="text" class="form-control" id="typepnInput" name="typepn" [(ngModel)]="addRailroadCrossingRequest.type_pn">
        </div>
        <div class="mb-3">
        <label for="typelcInput" class="form-label">Type</label>
        <input type="text" class="form-control" id="typelcInput" name="typelc" [(ngModel)]="addRailroadCrossingRequest.type_lc">
        </div>
        <div class="mb-3">
        <label for="positionXInput" class="form-label">Position X</label>
        <input type="text" class="form-control" id="positionXInput" name="positionX" [(ngModel)]="addRailroadCrossingRequest.fld_geo_x">
        </div>
        <div class="mb-3">
        <label for="positionYInput" class="form-label">Position Y</label>
        <input type="text" class="form-control" id="positionYInput" name="positionY" [(ngModel)]="addRailroadCrossingRequest.fld_geo_y">
        </div>
        <div class="mb-3">
        <label for="zipcodeInput" class="form-label">Zip code and municipality</label>
        <input type="text" class="form-control" id="zipcodeInput" name="zipcode" [(ngModel)]="addRailroadCrossingRequest.fld_postcode_en_gemeente">
        </div>
        <div class="mb-3">
        <label for="longitudeInput" class="form-label">Longitude</label>
        <input type="number" class="form-control" id="longitudeInput" name="longitude" [(ngModel)]="addRailroadCrossingRequest.position_du_passage_a_niveau.lon">
        </div>
        <div class="mb-3">
        <label for="latitudeInput" class="form-label">Latitude</label>
        <input type="number" class="form-control" id="latitudeInput" name="latitude" [(ngModel)]="addRailroadCrossingRequest.position_du_passage_a_niveau.lat">
        </div>
        <button type="submit" class="btn btn-primary">Edit</button>
    </form>
</div>
